mapper.select:
select * from(
	select c.*,rownum rn 
	from (select * from questionInfo) c
	where #{end} >= rownum
	 and questionType = #{type}
	 and content like '%'||#{content}||'%'
)
where rn > #{start}

select count(id) from questionInfo where 1=1
 and questionType = #{type}
 and content like '%'||#{content}||'%'
